import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { useGame } from '../contexts/GameContext';
import { useAuth } from '../contexts/AuthContext';
import { useSound } from '../contexts/SoundContext';
import EntityPortrait from '../components/EntityPortrait';
import toast from 'react-hot-toast';

const GamePage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { playSound } = useSound();
  const { user } = useAuth();
  const {
    currentSession,
    currentQuestion,
    gameState,
    questionHistory,
    timeRemaining,
    loading,
    startGameSession,
    fetchNextQuestion,
    submitAnswer,
    endGameSession,
    resetGame,
    getGameStats,
    setTimeRemaining,
  } = useGame();

  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [showExplanation, setShowExplanation] = useState(false);
  const [lastResult, setLastResult] = useState(null);
  const [hasError, setHasError] = useState(false);

  const searchParams = new URLSearchParams(location.search);
  const componentFilter = searchParams.get('component');

  useEffect(() => {
    if (gameState === 'idle' && user && !currentSession) {
      handleStartGame();
    }
  }, [gameState, user, currentSession]);

  useEffect(() => {
    let interval;
    if (gameState === 'playing' && timeRemaining > 0) {
      interval = setInterval(() => {
        setTimeRemaining(prev => prev - 1);
      }, 1000);
    } else if (timeRemaining === 0) {
      handleSubmitAnswer(null);
    }
    return () => clearInterval(interval);
  }, [gameState, timeRemaining]);

  const handleStartGame = async () => {
    playSound('button');
    setHasError(false);
    const result = await startGameSession(componentFilter || 'all');
    if (!result.success) {
      setHasError(true);
    }
  };

  const handleSubmitAnswer = async (answer) => {
    if (loading || selectedAnswer !== null) return;

    playSound('button');
    setSelectedAnswer(answer);
    setHasError(false);

    const result = await submitAnswer(answer);
    if (result.success) {
      setLastResult(result.result);
      setShowExplanation(true);

      setTimeout(() => {
        setShowExplanation(false);
        setSelectedAnswer(null);
        setLastResult(null);

        if (currentSession?.questionsAnswered < 65) {
          fetchNextQuestion(currentSession.id, componentFilter ? { category: componentFilter } : {});
        } else {
          endGameSession();
        }
      }, 5000);
    } else {
      setHasError(true);
      setSelectedAnswer(null);
    }
  };

  const handleSaveGame = () => {
    if (currentSession && currentQuestion) {
      const saveData = {
        session: currentSession,
        question: currentQuestion,
        questionHistory,
        timeRemaining,
        component: componentFilter,
        timestamp: new Date().toISOString(),
      };

      localStorage.setItem('cosmicGameSave', JSON.stringify(saveData));

      toast.success('Game saved to the cosmic void!', {
        icon: '💾',
        duration: 3000,
      });

      playSound('button');
    }
  };

  const handleReturnToMenu = () => {
    playSound('button');
    if (currentSession && gameState === 'playing') {
      const confirmLeave = window.confirm('Are you sure you want to abandon your cosmic journey? Progress will be lost unless saved.');
      if (!confirmLeave) return;
    }

    resetGame();
    navigate('/');
  };

  const handleRetryAfterError = () => {
    setHasError(false);
    setSelectedAnswer(null);
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  if (gameState === 'idle' || loading) {
    return (
      <GameContainer>
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
        >
          <GameCard className="cosmic-card">
            <Title className="cosmic-title">The Game Awakens</Title>
            <Subtitle>Summoning the cosmic questions...</Subtitle>
            <LoadingSpinner>🌀</LoadingSpinner>
          </GameCard>
        </motion.div>
      </GameContainer>
    );
  }

  if (gameState === 'ended') {
    const stats = getGameStats();
    return (
      <GameContainer>
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
        >
          <GameCard className="cosmic-card">
            <Title className="cosmic-title">Ritual Complete</Title>
            <Subtitle>Your cosmic journey has ended...</Subtitle>

            <StatsContainer>
              <StatItem>
                <StatLabel>Questions Answered:</StatLabel>
                <StatValue>{stats.questionsAnswered}</StatValue>
              </StatItem>
              <StatItem>
                <StatLabel>Correct Answers:</StatLabel>
                <StatValue>{stats.correctAnswers}</StatValue>
              </StatItem>
              <StatItem>
                <StatLabel>Accuracy:</StatLabel>
                <StatValue>{stats.accuracy}%</StatValue>
              </StatItem>
            </StatsContainer>

            <ButtonContainer>
              <ActionButton onClick={handleReturnToMenu}>
                Return to Cosmic Menu
              </ActionButton>
              <ActionButton onClick={handleStartGame}>
                Begin New Ritual
              </ActionButton>
            </ButtonContainer>
          </GameCard>
        </motion.div>
      </GameContainer>
    );
  }

  return (
    <GameContainer>
      <GameHeader>
        <GameControls>
          <SaveButton onClick={handleSaveGame}>
            💾 Save Game
          </SaveButton>
          <MenuButton onClick={handleReturnToMenu}>
            🏠 Return to Menu
          </MenuButton>
        </GameControls>

        <GameInfo>
          <InfoItem>
            <InfoLabel>Question:</InfoLabel>
            <InfoValue>{currentSession?.questionsAnswered + 1}/65</InfoValue>
          </InfoItem>
          <InfoItem>
            <InfoLabel>Time:</InfoLabel>
            <InfoValue>{formatTime(timeRemaining)}</InfoValue>
          </InfoItem>
          <InfoItem>
            <InfoLabel>Accuracy:</InfoLabel>
            <InfoValue>
              {currentSession?.questionsAnswered > 0 
                ? ((currentSession.correctAnswers / currentSession.questionsAnswered) * 100).toFixed(1)
                : 0}%
            </InfoValue>
          </InfoItem>
        </GameInfo>
      </GameHeader>

      {currentQuestion && (
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          <QuestionCard className="cosmic-card">
            {currentQuestion.cosmicContext && (
              <EntitySection>
                <EntityPortrait 
                  entity={{
                    name: currentQuestion.cosmicContext.entityName,
                    emoji: currentQuestion.cosmicContext.entityEmoji || "👁️",
                    description: currentQuestion.cosmicContext.description
                  }}
                  size="medium"
                />
                <EntityFlavor>
                  {currentQuestion.cosmicContext.flavorText}
                </EntityFlavor>
              </EntitySection>
            )}

            <QuestionText>{currentQuestion.question}</QuestionText>

            <OptionsContainer>
              {currentQuestion.options.map((option, index) => (
                <OptionButton
                  key={index}
                  onClick={() => !hasError && handleSubmitAnswer(index)}
                  disabled={loading || selectedAnswer !== null}
                  selected={selectedAnswer === index}
                  className={selectedAnswer === index ? 'selected' : ''}
                >
                  {String.fromCharCode(65 + index)}. {option}
                </OptionButton>
              ))}
            </OptionsContainer>

            {hasError && (
              <ErrorContainer>
                <ErrorMessage>
                  The cosmic forces disrupted your answer. Try selecting a different option.
                </ErrorMessage>
                <RetryButton onClick={handleRetryAfterError}>
                  Choose Another Path
                </RetryButton>
              </ErrorContainer>
            )}
          </QuestionCard>
        </motion.div>
      )}

      {showExplanation && lastResult && (
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          <ExplanationCard className="cosmic-card">
            <ExplanationHeader correct={lastResult.correct}>
              {lastResult.correct ? '✨ Correct!' : '💀 Incorrect'}
            </ExplanationHeader>
            <ExplanationText>
              {lastResult.explanation}
            </ExplanationText>
            {lastResult.cosmicResponse && (
              <CosmicResponse>
                {lastResult.cosmicResponse}
              </CosmicResponse>
            )}
          </ExplanationCard>
        </motion.div>
      )}
    </GameContainer>
  );
};

const GameContainer = styled.div`
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  padding: 1rem;
  max-width: 1200px;
  margin: 0 auto;
`;

const GameHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 2rem;
  padding: 1rem;
  background: ${props => props.theme.colors.backgroundMedium};
  border-radius: 12px;
  border: 1px solid ${props => props.theme.colors.accent};

  @media (max-width: 768px) {
    flex-direction: column;
    gap: 1rem;
  }
`;

const GameControls = styled.div`
  display: flex;
  gap: 1rem;
`;

const SaveButton = styled.button`
  background: linear-gradient(45deg, #2196F3, #21CBF3);
  color: white;
  border: none;
  padding: 0.5rem 1rem;
  border-radius: 8px;
  cursor: pointer;
  font-size: 0.9rem;
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(33, 150, 243, 0.3);
  }
`;

const MenuButton = styled.button`
  background: linear-gradient(45deg, #FF6B6B, #FF8E8E);
  color: white;
  border: none;
  padding: 0.5rem 1rem;
  border-radius: 8px;
  cursor: pointer;
  font-size: 0.9rem;
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(255, 107, 107, 0.3);
  }
`;

const GameInfo = styled.div`
  display: flex;
  gap: 2rem;

  @media (max-width: 768px) {
    gap: 1rem;
  }
`;

const InfoItem = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const InfoLabel = styled.span`
  font-size: 0.8rem;
  color: ${props => props.theme.colors.textSecondary};
`;

const InfoValue = styled.span`
  font-size: 1.2rem;
  font-weight: bold;
  color: ${props => props.theme.colors.primary};
`;

const GameCard = styled.div`
  max-width: 800px;
  width: 100%;
  text-align: center;
  margin: 0 auto;
`;

const QuestionCard = styled.div`
  margin-bottom: 2rem;
  text-align: left;
`;

const EntitySection = styled.div`
  display: flex;
  align-items: center;
  gap: 1rem;
  margin-bottom: 2rem;
  padding: 1rem;
  background: ${props => props.theme.colors.backgroundDark};
  border-radius: 8px;
  border: 1px solid ${props => props.theme.colors.secondary};

  @media (max-width: 768px) {
    flex-direction: column;
    text-align: center;
  }
`;

const EntityFlavor = styled.p`
  color: ${props => props.theme.colors.accent};
  font-style: italic;
  font-size: 0.9rem;
  margin: 0;
`;

const QuestionText = styled.p`
  font-size: 1.1rem;
  line-height: 1.6;
  margin-bottom: 2rem;
  color: ${props => props.theme.colors.text};
`;

const OptionsContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 1rem;
`;

const OptionButton = styled.button`
  background: ${props => props.selected 
    ? `linear-gradient(45deg, ${props.theme.colors.primary}, ${props.theme.colors.secondary})`
    : `linear-gradient(135deg, ${props.theme.colors.backgroundMedium}, ${props.theme.colors.surface})`
  };
  color: ${props => props.theme.colors.text};
  border: 2px solid ${props => props.selected 
    ? props.theme.colors.primary 
    : props.theme.colors.accent
  };
  padding: 1rem;
  border-radius: 8px;
  cursor: pointer;
  transition: all 0.3s ease;
  text-align: left;
  font-size: 1rem;

  &:hover:not(:disabled) {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(116, 0, 184, 0.3);
    border-color: ${props => props.theme.colors.primary};
  }

  &:disabled {
    opacity: 0.7;
    cursor: not-allowed;
  }
`;

const ErrorContainer = styled.div`
  margin-top: 2rem;
  padding: 1rem;
  background: linear-gradient(135deg, #FF6B6B, #FF8E8E);
  border-radius: 8px;
  text-align: center;
`;

const ErrorMessage = styled.p`
  color: white;
  margin-bottom: 1rem;
  font-size: 1rem;
`;

const RetryButton = styled.button`
  background: white;
  color: #FF6B6B;
  border: none;
  padding: 0.5rem 1rem;
  border-radius: 8px;
  cursor: pointer;
  font-weight: bold;
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(255, 255, 255, 0.3);
  }
`;

const ExplanationCard = styled.div`
  margin-top: 2rem;
  text-align: left;
`;

const ExplanationHeader = styled.h3`
  color: ${props => props.correct ? '#00ff88' : '#ff0066'};
  margin-bottom: 1rem;
  font-size: 1.5rem;
`;

const ExplanationText = styled.p`
  color: ${props => props.theme.colors.text};
  line-height: 1.6;
  margin-bottom: 1rem;
`;

const CosmicResponse = styled.p`
  color: ${props => props.theme.colors.accent};
  font-style: italic;
  font-size: 0.9rem;
  margin: 0;
`;

const StatsContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 1rem;
  margin: 2rem 0;
`;

const StatItem = styled.div`
  display: flex;
  justify-content: space-between;
  padding: 1rem;
  background: ${props => props.theme.colors.backgroundMedium};
  border-radius: 8px;
  border: 1px solid ${props => props.theme.colors.accent};
`;

const StatLabel = styled.span`
  color: ${props => props.theme.colors.textSecondary};
`;

const StatValue = styled.span`
  color: ${props => props.theme.colors.primary};
  font-weight: bold;
`;

const ButtonContainer = styled.div`
  display: flex;
  gap: 1rem;
  justify-content: center;
  margin-top: 2rem;

  @media (max-width: 768px) {
    flex-direction: column;
  }
`;

const ActionButton = styled.button`
  background: linear-gradient(45deg, ${props => props.theme.colors.primary}, ${props => props.theme.colors.secondary});
  color: white;
  border: none;
  padding: 1rem 2rem;
  border-radius: 8px;
  cursor: pointer;
  font-size: 1rem;
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(116, 0, 184, 0.3);
  }
`;

const Title = styled.h1`
  font-size: 3rem;
  margin-bottom: 1rem;

  @media (max-width: 768px) {
    font-size: 2rem;
  }
`;

const Subtitle = styled.p`
  color: ${props => props.theme.colors.textSecondary};
  margin-bottom: 2rem;
  font-size: 1.2rem;
`;

const LoadingSpinner = styled.div`
  font-size: 3rem;
  animation: spin 2s linear infinite;

  @keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }
`;

export default GamePage;
