import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import styled from 'styled-components';
import { useAuth } from '../contexts/AuthContext';
import { useSound } from '../contexts/SoundContext';
import CosmicParticles from '../components/CosmicParticles';
import EntityPortrait from '../components/EntityPortrait';

const HomePage = () => {
  const navigate = useNavigate();
  const { user } = useAuth();
  const { playSound } = useSound();
  const [currentEntity, setCurrentEntity] = useState(0);

  const cosmicEntities = [
    {
      name: "Azathoth",
      title: "The Blind Idiot God of Scaling",
      description: "Master of Auto Scaling and Load Balancing nightmares",
      emoji: "🌀"
    },
    {
      name: "Cthulhu",
      title: "The Dreaming Database",
      description: "Sleeps in the depths of RDS until queries awaken it",
      emoji: "🐙"
    },
    {
      name: "Nyarlathotep",
      title: "The Crawling Chaos of Networks",
      description: "Weaves the dark networks connecting all digital realms",
      emoji: "🕷️"
    },
    {
      name: "Shub-Niggurath",
      title: "The Black Goat of Storage",
      description: "Mother of all S3 buckets and infinite storage spawn",
      emoji: "🐐"
    },
    {
      name: "Yog-Sothoth",
      title: "The Key and the Gate",
      description: "Guardian of all IAM permissions and security gates",
      emoji: "🗝️"
    }
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentEntity((prev) => (prev + 1) % cosmicEntities.length);
    }, 4000);

    return () => clearInterval(interval);
  }, []);

  const handleStartGame = () => {
    playSound('button');
    if (user) {
      navigate('/game');
    } else {
      navigate('/login');
    }
  };

  const handleLeaderboard = () => {
    playSound('button');
    navigate('/leaderboard');
  };

  const handleComponentStudy = (component) => {
    playSound('button');
    if (user) {
      navigate(`/game?component=${component}`);
    } else {
      navigate('/login');
    }
  };

  return (
    <HomeContainer>
      <CosmicParticles />

      <ContentWrapper>
        <motion.div
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1 }}
        >
          <MainTitle className="modern-title">
            The Architect's Codex
          </MainTitle>
          <Subtitle>
            Cosmic Horror AWS Certification Study Experience
          </Subtitle>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, scale: 0.8 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 0.8, delay: 0.5 }}
        >
          <EntityShowcase>
            <EntityPortrait 
              entity={cosmicEntities[currentEntity]} 
              size="large"
            />
            <EntityDescription>
              <EntityName>{cosmicEntities[currentEntity].name}</EntityName>
              <EntityTitle>{cosmicEntities[currentEntity].title}</EntityTitle>
              <EntityText>{cosmicEntities[currentEntity].description}</EntityText>
            </EntityDescription>
          </EntityShowcase>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 1 }}
        >
          <GameDescription className="modern-card">
            <h2>Master AWS Architecture</h2>
            <p>
              Master AWS Solutions Architect Associate certification through interactive learning. 
              Build real-world cloud expertise with scenario-based questions, track your progress, 
              and compete with other architects on your journey to certification success.
            </p>

            <FeatureList>
              <FeatureItem>
                <span className="feature-icon">📚</span>
                <strong>Exam-Ready Questions:</strong> 65 scenario-based questions matching real AWS certification difficulty
              </FeatureItem>
              <FeatureItem>
                <span className="feature-icon">🎯</span>
                <strong>Component-Based Learning:</strong> Study by AWS service categories for focused preparation
              </FeatureItem>
              <FeatureItem>
                <span className="feature-icon">📊</span>
                <strong>Progress Tracking:</strong> Monitor your accuracy, knowledge growth, and certification readiness
              </FeatureItem>
              <FeatureItem>
                <span className="feature-icon">🏆</span>
                <strong>Competitive Leaderboard:</strong> Compete with other architects and track your ranking
              </FeatureItem>
            </FeatureList>
          </GameDescription>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 1.2 }}
        >
          <ButtonContainer>
            <ActionButton 
              className="modern-button primary" 
              onClick={handleStartGame}
            >
              {user ? 'Continue Learning' : 'Start Practice'}
            </ActionButton>
            <ActionButton 
              className="modern-button secondary" 
              onClick={handleLeaderboard}
            >
              View Leaderboard
            </ActionButton>
          </ButtonContainer>

          <ComponentsMenu className="modern-card">
            <h2>Study by AWS Service Category</h2>
            <ComponentGrid>
              <ComponentCard onClick={() => handleComponentStudy('storage')}>
                <ComponentIcon>💾</ComponentIcon>
                <ComponentTitle>Storage</ComponentTitle>
                <ComponentDesc>S3, EBS, EFS, FSx</ComponentDesc>
              </ComponentCard>
              <ComponentCard onClick={() => handleComponentStudy('processing')}>
                <ComponentIcon>⚡</ComponentIcon>
                <ComponentTitle>Processing</ComponentTitle>
                <ComponentDesc>EC2, Lambda, ECS, Batch</ComponentDesc>
              </ComponentCard>
              <ComponentCard onClick={() => handleComponentStudy('pipelines')}>
                <ComponentIcon>🔄</ComponentIcon>
                <ComponentTitle>Pipelines</ComponentTitle>
                <ComponentDesc>CodePipeline, CodeBuild, CodeDeploy</ComponentDesc>
              </ComponentCard>
              <ComponentCard onClick={() => handleComponentStudy('roles')}>
                <ComponentIcon>🔐</ComponentIcon>
                <ComponentTitle>Roles & Permissions</ComponentTitle>
                <ComponentDesc>IAM, Security Groups, NACLs</ComponentDesc>
              </ComponentCard>
              <ComponentCard onClick={() => handleComponentStudy('networking')}>
                <ComponentIcon>🌐</ComponentIcon>
                <ComponentTitle>Networking</ComponentTitle>
                <ComponentDesc>VPC, Route 53, CloudFront</ComponentDesc>
              </ComponentCard>
              <ComponentCard onClick={() => handleComponentStudy('database')}>
                <ComponentIcon>🗄️</ComponentIcon>
                <ComponentTitle>Database</ComponentTitle>
                <ComponentDesc>RDS, DynamoDB, ElastiCache</ComponentDesc>
              </ComponentCard>
            </ComponentGrid>
          </ComponentsMenu>
        </motion.div>

        {user && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.8, delay: 1.4 }}
          >
            <WelcomeMessage className="modern-card">
              Welcome back, {user.username}. Ready to continue your AWS learning journey?
            </WelcomeMessage>
          </motion.div>
        )}
      </ContentWrapper>
    </HomeContainer>
  );
};

const HomeContainer = styled.div`
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 2rem;
  position: relative;
  overflow: hidden;
`;

const ContentWrapper = styled.div`
  max-width: 1200px;
  width: 100%;
  text-align: center;
  position: relative;
  z-index: 1;
`;

const MainTitle = styled.h1`
  font-size: 4rem;
  margin-bottom: 1rem;

  @media (max-width: 768px) {
    font-size: 2.5rem;
  }
`;

const Subtitle = styled.h2`
  font-size: 1.5rem;
  color: ${props => props.theme.colors.textSecondary};
  margin-bottom: 3rem;
  font-family: ${props => props.theme.fonts.button};

  @media (max-width: 768px) {
    font-size: 1.2rem;
  }
`;

const EntityShowcase = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  margin: 3rem 0;

  @media (min-width: 768px) {
    flex-direction: row;
    justify-content: center;
    gap: 3rem;
  }
`;

const EntityDescription = styled.div`
  text-align: center;
  max-width: 400px;

  @media (min-width: 768px) {
    text-align: left;
  }
`;

const EntityName = styled.h3`
  font-size: 2rem;
  color: ${props => props.theme.colors.secondary};
  margin-bottom: 0.5rem;
  font-family: ${props => props.theme.fonts.horror};
`;

const EntityTitle = styled.h4`
  font-size: 1.2rem;
  color: ${props => props.theme.colors.primary};
  margin-bottom: 1rem;
  font-family: ${props => props.theme.fonts.button};
`;

const EntityText = styled.p`
  font-size: 1rem;
  color: ${props => props.theme.colors.textSecondary};
  line-height: 1.6;
`;

const GameDescription = styled.div`
  margin: 3rem 0;
  text-align: left;

  h2 {
    text-align: center;
    margin-bottom: 2rem;
    color: ${props => props.theme.colors.primary};
  }

  p {
    font-size: 1.1rem;
    line-height: 1.8;
    margin-bottom: 2rem;
    color: ${props => props.theme.colors.textSecondary};
  }
`;

const FeatureList = styled.ul`
  list-style: none;
  padding: 0;
  margin: 2rem 0;
`;

const FeatureItem = styled.li`
  display: flex;
  align-items: flex-start;
  margin-bottom: 1.5rem;

  .feature-icon {
    font-size: 1.5rem;
    margin-right: 1rem;
    flex-shrink: 0;
  }

  strong {
    color: ${props => props.theme.colors.accent};
    margin-right: 0.5rem;
  }
`;

const ButtonContainer = styled.div`
  display: flex;
  gap: 2rem;
  justify-content: center;
  margin: 3rem 0;

  @media (max-width: 768px) {
    flex-direction: column;
    gap: 1rem;
  }
`;

const ActionButton = styled.button`
  font-size: 1.2rem;
  padding: 1rem 2rem;

  &.primary {
    background: linear-gradient(45deg, ${props => props.theme.colors.primary}, ${props => props.theme.colors.secondary});
  }

  &.secondary {
    background: linear-gradient(45deg, ${props => props.theme.colors.backgroundMedium}, ${props => props.theme.colors.surface});
  }

  @media (max-width: 768px) {
    font-size: 1rem;
    padding: 0.8rem 1.5rem;
  }
`;

const WelcomeMessage = styled.div`
  margin-top: 2rem;
  font-size: 1.1rem;
  color: ${props => props.theme.colors.accent};
  text-align: center;
`;

const ComponentsMenu = styled.div`
  margin: 3rem 0;

  h2 {
    text-align: center;
    margin-bottom: 2rem;
    color: ${props => props.theme.colors.primary};
  }
`;

const ComponentGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 1.5rem;
  margin-top: 2rem;
`;

const ComponentCard = styled.button`
  background: linear-gradient(135deg, ${props => props.theme.colors.surface}, ${props => props.theme.colors.surfaceHover});
  border: 1px solid ${props => props.theme.colors.border};
  border-radius: 16px;
  padding: 1.5rem;
  cursor: pointer;
  transition: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);
  text-align: center;
  box-shadow: ${props => props.theme.shadows.sm};

  &:hover {
    transform: translateY(-2px);
    box-shadow: ${props => props.theme.shadows.md};
    border-color: ${props => props.theme.colors.primary};
    background: linear-gradient(135deg, ${props => props.theme.colors.surfaceHover}, ${props => props.theme.colors.backgroundMedium});
  }
`;

const ComponentIcon = styled.div`
  font-size: 2.5rem;
  margin-bottom: 1rem;
`;

const ComponentTitle = styled.h3`
  color: ${props => props.theme.colors.text};
  margin-bottom: 0.5rem;
  font-size: 1.2rem;
  font-weight: 600;
`;

const ComponentDesc = styled.p`
  color: ${props => props.theme.colors.textMuted};
  font-size: 0.9rem;
  margin: 0;
  line-height: 1.4;
`;

export default HomePage;
