import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { useGame } from '../contexts/GameContext';
import { useAuth } from '../contexts/AuthContext';
import { useSound } from '../contexts/SoundContext';
import EntityPortrait from '../components/EntityPortrait';
import toast from 'react-hot-toast';

const GamePage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { playSound } = useSound();
  const { user } = useAuth();
  const {
    currentSession,
    currentQuestion,
    gameState,
    questionHistory,
    timeRemaining,
    loading,
    startGameSession,
    fetchNextQuestion,
    submitAnswer,
    endGameSession,
    resetGame,
    getGameStats,
    setTimeRemaining,
  } = useGame();

  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [showExplanation, setShowExplanation] = useState(false);
  const [lastResult, setLastResult] = useState(null);
  const [hasError, setHasError] = useState(false);

  const searchParams = new URLSearchParams(location.search);
  const componentFilter = searchParams.get('component');

  useEffect(() => {
    if (gameState === 'idle' && user && !currentSession) {
      handleStartGame();
    }
  }, [gameState, user, currentSession]);

  useEffect(() => {
    let interval;
    if (gameState === 'playing' && timeRemaining > 0) {
      interval = setInterval(() => {
        setTimeRemaining(prev => prev - 1);
      }, 1000);
    } else if (timeRemaining === 0) {
      handleSubmitAnswer(null);
    }
    return () => clearInterval(interval);
  }, [gameState, timeRemaining]);

  const handleStartGame = async () => {
    playSound('button');
    setHasError(false);
    const result = await startGameSession(componentFilter || 'all');
    if (!result.success) {
      setHasError(true);
    }
  };

  const handleSubmitAnswer = async (answer) => {
    if (loading || selectedAnswer !== null) return;

    playSound('button');
    setSelectedAnswer(answer);
    setHasError(false);

    const result = await submitAnswer(answer);
    if (result.success) {
      setLastResult(result.result);
      setShowExplanation(true);

      setTimeout(() => {
        setShowExplanation(false);
        setSelectedAnswer(null);
        setLastResult(null);

        if (currentSession?.questionsAnswered < 65) {
          fetchNextQuestion(currentSession.id, componentFilter ? { category: componentFilter } : {});
        } else {
          endGameSession();
        }
      }, 5000);
    } else {
      setHasError(true);
      setSelectedAnswer(null);
    }
  };

  const handleSaveGame = () => {
    if (currentSession && currentQuestion) {
      const saveData = {
        session: currentSession,
        question: currentQuestion,
        questionHistory,
        timeRemaining,
        component: componentFilter,
        timestamp: new Date().toISOString(),
      };

      localStorage.setItem('cosmicGameSave', JSON.stringify(saveData));

      toast.success('Game saved to the cosmic void!', {
        icon: 'üíæ',
        duration: 3000,
      });

      playSound('button');
    }
  };

  const handleReturnToMenu = () => {
    playSound('button');
    if (currentSession && gameState === 'playing') {
      const confirmLeave = window.confirm('Are you sure you want to abandon your cosmic journey? Progress will be lost unless saved.');
      if (!confirmLeave) return;
    }

    resetGame();
    navigate('/');
  };

  const handleRetryAfterError = () => {
    setHasError(false);
    setSelectedAnswer(null);
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  if (gameState === 'idle' || loading) {
    return (
      <GameContainer>
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
        >
          <GameCard className="modern-card">
            <Title className="modern-title">Practice Session Loading</Title>
            <Subtitle>Preparing your AWS questions...</Subtitle>
            <LoadingSpinner>
              <div className="spinner"></div>
            </LoadingSpinner>
          </GameCard>
        </motion.div>
      </GameContainer>
    );
  }

  if (gameState === 'ended') {
    const stats = getGameStats();
    return (
      <GameContainer>
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
        >
          <GameCard className="modern-card">
            <Title className="modern-title">Session Complete</Title>
            <Subtitle>Great work! Here are your results:</Subtitle>

            <StatsContainer>
              <StatItem>
                <StatLabel>Questions Answered:</StatLabel>
                <StatValue>{stats.questionsAnswered}</StatValue>
              </StatItem>
              <StatItem>
                <StatLabel>Correct Answers:</StatLabel>
                <StatValue>{stats.correctAnswers}</StatValue>
              </StatItem>
              <StatItem>
                <StatLabel>Accuracy:</StatLabel>
                <StatValue>{stats.accuracy}%</StatValue>
              </StatItem>
            </StatsContainer>

            <ButtonContainer>
              <ActionButton onClick={handleReturnToMenu}>
                Return to Main Menu
              </ActionButton>
              <ActionButton onClick={handleStartGame}>
                Start New Session
              </ActionButton>
            </ButtonContainer>
          </GameCard>
        </motion.div>
      </GameContainer>
    );
  }

  return (
    <GameContainer>
      <GameHeader>
        <GameControls>
          <SaveButton onClick={handleSaveGame}>
            üíæ Save Game
          </SaveButton>
          <MenuButton onClick={handleReturnToMenu}>
            üè† Return to Menu
          </MenuButton>
        </GameControls>

        <GameInfo>
          <InfoItem>
            <InfoLabel>Question:</InfoLabel>
            <InfoValue>{currentSession?.questionsAnswered + 1}/65</InfoValue>
          </InfoItem>
          <InfoItem>
            <InfoLabel>Time:</InfoLabel>
            <InfoValue>{formatTime(timeRemaining)}</InfoValue>
          </InfoItem>
          <InfoItem>
            <InfoLabel>Accuracy:</InfoLabel>
            <InfoValue>
              {currentSession?.questionsAnswered > 0 
                ? ((currentSession.correctAnswers / currentSession.questionsAnswered) * 100).toFixed(1)
                : 0}%
            </InfoValue>
          </InfoItem>
        </GameInfo>
      </GameHeader>

      {currentQuestion && (
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          <QuestionCard className="modern-card">
            {currentQuestion.cosmicContext && (
              <EntitySection>
                <EntityPortrait 
                  entity={{
                    name: currentQuestion.cosmicContext.entityName,
                    emoji: currentQuestion.cosmicContext.entityEmoji || "üëÅÔ∏è",
                    description: currentQuestion.cosmicContext.description
                  }}
                  size="medium"
                />
                <EntityFlavor>
                  {currentQuestion.cosmicContext.flavorText}
                </EntityFlavor>
              </EntitySection>
            )}

            <QuestionText>{currentQuestion.question}</QuestionText>

            <OptionsContainer>
              {currentQuestion.options.map((option, index) => (
                <OptionButton
                  key={index}
                  onClick={() => !hasError && handleSubmitAnswer(index)}
                  disabled={loading || selectedAnswer !== null}
                  selected={selectedAnswer === index}
                  className={selectedAnswer === index ? 'selected' : ''}
                >
                  {String.fromCharCode(65 + index)}. {option}
                </OptionButton>
              ))}
            </OptionsContainer>

            {hasError && (
              <ErrorContainer>
                <ErrorMessage>
                  The cosmic forces disrupted your answer. Try selecting a different option.
                </ErrorMessage>
                <RetryButton onClick={handleRetryAfterError}>
                  Choose Another Path
                </RetryButton>
              </ErrorContainer>
            )}
          </QuestionCard>
        </motion.div>
      )}

      {showExplanation && lastResult && (
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          <ExplanationCard className="modern-card">
            <ExplanationHeader correct={lastResult.correct}>
              {lastResult.correct ? '‚ú® Correct!' : 'üíÄ Incorrect'}
            </ExplanationHeader>
            <ExplanationText>
              {lastResult.explanation}
            </ExplanationText>
            {lastResult.cosmicResponse && (
              <CosmicResponse>
                {lastResult.cosmicResponse}
              </CosmicResponse>
            )}
          </ExplanationCard>
        </motion.div>
      )}
    </GameContainer>
  );
};

const GameContainer = styled.div`
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  padding: 1rem;
  max-width: 1200px;
  margin: 0 auto;
`;

const GameHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 2rem;
  padding: 1rem;
  background: ${props => props.theme.colors.surface};
  border-radius: 16px;
  border: 1px solid ${props => props.theme.colors.border};
  box-shadow: ${props => props.theme.shadows.sm};

  @media (max-width: 768px) {
    flex-direction: column;
    gap: 1rem;
  }
`;

const GameControls = styled.div`
  display: flex;
  gap: 1rem;
